	import random

	root = Tk()

	def table_of_random_widgets(master, numRows,numColumns, **kwargs):
		import ttk

		demo = testConnection.create_widget(master, shared_columns ='all', shared_panedwindows='all', **kwargs)

		tkinterWidgets =  (
					(Label, {'text':'This is a label'}),
					(Label, {'text':'This is another label', 'bg':'yellow'}),
					(Checkbutton,{}),
					(Button, {'text':'Click me'}),
					(ttk.Combobox, {'values':('item1', 'item2','item3','item4')})
					)

		for i in range(numRows):
			for j in range(numColumns):
				widgetClass, args = random.choice(tkinterWidgets)
				widget = widgetClass(demo,**args)

				demo[i,j] =  widget

		return demo
	testConnection = connected_Panedgrids(0,6)

	table1 = table_of_random_widgets(root, numRows=4,numColumns=6, fixedPanes=[2,5])
	table2 = table_of_random_widgets(root, numRows=4,numColumns=6, fixedPanes=[2,5])

	testConnection.update()

	Label(root, text= "Two different paned grids connected. Columns 2 and 5 are fixed.").pack(anchor=NW,pady=7, padx=12)
	table1.pack(anchor=NW,pady=2, padx=12)

	emptySpace = Frame(root, height =50)
	emptySpace.pack()

	table2.pack(anchor=NW,pady=2, padx=12)

	emptySpace = Frame(root, height =20)
	emptySpace.pack()


	root.mainloop()


if __name__ == '__main__':
	test()

from app import app
from . import requires_login
from flask import redirect, url_for, flash, session, render_template, request
from rauth.service import OAuth2Service

github = OAuth2Service(
    name='github',
    base_url='https://api.github.com/user',
    authorize_url='https://github.com/login/oauth/authorize',
    access_token_url='https://github.com/login/oauth/access_token',
    client_id=app.config['GITHUB_CLIENT_ID'],
    client_secret=app.config['GITHUB_CLIENT_SECRET']
)

@app.route('/github_login')
@requires_login
def github_login():
    """
    Authenticate with Github.
    """
    redirect_uri = url_for('github_authorized', _external=True)
    return redirect(github.get_authorize_url(redirect_uri=redirect_uri, scope='user,repo'))

@app.route('/github')
@requires_login
def github_info():
    """
    Provide some info about linking
    a Github account.
    """
    return render_template('github.html')

# After authentication.
@app.route(app.config['GITHUB_REDIRECT_URI'])
def github_authorized():
    """
    Post-authentication redirect route.
    Adds access token to the session.
    """

    if not 'code' in request.args:
        flash(u'Whoops, you denied us access to your Github account')
        return redirect(url_for('github_login'))

    redirect_uri = url_for('github_authorized', _external=True)
    data = dict(code=request.args['code'], redirect_uri=redirect_uri, scope='user,repo')
    session_ = github.get_auth_session(data=data)

    # Store access token in session.
    session['github_access_token'] = session_.access_token

    from app.models.user import current_user
    current_user = current_user()
    current_user.github_id = session_.get('/user').json()['id']
    current_user.github_access = session_.access_token
    current_user.save()

    # Redirect
    return redirect('/')

def api(token=None):
    if token is None:
        token = session['github_access_token']
    return github.get_session(token=token)

