import asyncio
from datetime import timedelta
from unittest import mock

import pytest

from gator.aiojob import Job


def test_job_unique_jid():
    jobs = [Job() for _ in range(1000)]
    jids = {job.jid for job in jobs}
    assert len(jobs) == len(jids)


@pytest.fixture
def job():
    return Job()


@pytest.fixture
def pending_job(job):
    return job


def test_pending_job_start(pending_job):
    pending_job.start()
    assert pending_job.state() == 'RUNNING'


def test_pending_job_done(pending_job):
    assert not pending_job.done()


def test_pending_job_cancelled(pending_job):
    assert not pending_job.cancelled()


def test_pending_job_state(pending_job):
    assert pending_job.state() == 'PENDING'


def test_pending_job_result(pending_job):
    with pytest.raises(asyncio.InvalidStateError):
        pending_job.result()


def test_pending_job_exception(pending_job):
    with pytest.raises(asyncio.InvalidStateError):
        pending_job.exception()


def test_pending_job_cancel(pending_job):
    assert pending_job.cancel()
    assert pending_job.cancelled()
    assert pending_job.state() == 'CANCELLED'


def test_pending_job_set_result(pending_job):
    pending_job.set_result(True)
    assert pending_job.done()
    assert pending_job.state() == 'DONE'
    assert pending_job.duration() == timedelta(0)


def test_pending_job_set_exception(pending_job):
    pending_job.set_exception(Exception())
    assert pending_job.exception()


def test_pending_job_duration(pending_job):
    with pytest.raises(asyncio.InvalidStateError):
        pending_job.duration()


