@pytest.mark.asyncio
async def test_job_await_done(event_loop):
    job = Job(loop=event_loop)

    callback = mock.MagicMock(job)
    job.add_done_callback(callback)

    async def set_result(job):
        job.set_result(True)

    asyncio.ensure_future(set_result(job), loop=event_loop)
    await asyncio.wait_for(job, 5, loop=event_loop)
    assert job.state() == 'DONE'
    assert callback.called
    assert callback.call_args_list == [mock.call(job)]


@pytest.mark.asyncio
async def test_job_await_cancelled(event_loop):
    job = Job(loop=event_loop)

    callback = mock.MagicMock(job)
    job.add_done_callback(callback)

    async def cancel(job):
        job.cancel()

    asyncio.ensure_future(cancel(job), loop=event_loop)
    with pytest.raises(asyncio.CancelledError):
        await asyncio.wait_for(job, 5, loop=event_loop)
    assert job.state() == 'CANCELLED'
    assert callback.called
    assert callback.call_args_list == [mock.call(job)]


@pytest.mark.asyncio
async def test_job_await_error(event_loop):
    job = Job(loop=event_loop)

    callback = mock.MagicMock(job)
    job.add_done_callback(callback)

    class SomeException(Exception):
        pass

    async def set_exception(job):
